import { Component } from '@angular/core';
import { Product } from '../productlist/product';

@Component({
  selector: 'product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css']
})
export class ProductListComponent {
  //name="John Smith";
  addToCart:number = 0;

products: Product[] = [
    {
      name: 'iPhone X',
      price: 789,
      color: 'Black',
      discount: 8.5,
      inStock: 10,
      pImage: '/assets/images/iphone.png'
    },
    {
      name: 'Samsung Galaxy S10',
      price: 899,
      color: 'White',
      discount: 10,
      inStock: 15,
      pImage: '/assets/images/galaxy.png'
    },
    {
      name: 'Google Pixel 4',
      price: 799,
      color: 'Blue',
      discount: 5,
      inStock: 5,
      pImage: '/assets/images/pixel.png'
    },
    {
      name: 'OnePlus 7T',
      price: 699,
      color: 'Gray',
      discount: 7,
      inStock: 8,
      pImage: '/assets/images/oneplus.png'
    },
    {
      name: 'Huawei P30',
      price: 749,
      color: 'Black',
      discount: 6,
      inStock: 12,
      pImage: '/assets/images/huawei.png'
    }
  ];
}
